PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX schema: <https://schema.org/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

#PErsonen voor een programma (ook via series/seizoenj/scene description)

SELECT ?entity ?entityName where {
  {
  <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> a schema:CreativeWork ; (schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer)/(schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer) ?entity  .
  ?entity skos:prefLabel ?entityName 
  }
  UNION
    {
  <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> schema:isPartOfSeason ?season . ?season  (schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer)/(schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer) ?entity  .
  ?entity skos:prefLabel ?entityName 
  }
  UNION
    {
  <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> schema:isPartOfSeason/schema:isPartOfSeries ?series . ?series  (schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer)/(schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer) ?entity  .
  ?entity skos:prefLabel ?entityName 
  }
  UNION
    {
  ?scene schema:hasPart <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931>. ?scene  (schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer)/(schema:about|schema:mentions|schema:creator|schema:contributor|schema:actor|schema:crew|schema:performer) ?entity  .
  ?entity skos:prefLabel ?entityName 
  }
}
